mapper.select:
SELECT	A.MB_ID	MB_ID,
		A.MB_PW	MB_PW
FROM	NET_MEMBER A
WHERE	A.MB_DELYN = 'Y'
AND		A.MB_ID = #{mb_id, jdbcType=VARCHAR}
AND		A.MB_PW = #{mb_pw, jdbcType=VARCHAR}

SELECT	A.MB_NAME		MB_NAME,
		A.MB_EMAIL		MB_EMAIL
FROM	NET_MEMBER A
WHERE	A.MB_DELYN = 'Y'
AND		A.MB_NAME = #{mb_name, jdbcType=VARCHAR}
AND		A.MB_EMAIL = #{mb_email, jdbcType=VARCHAR}

SELECT	A.MB_ID		MB_ID,
		A.MB_EMAIL	MB_EMAIL
FROM	NET_MEMBER A
WHERE	A.MB_DELYN = 'Y'
AND		A.MB_ID = #{mb_id, jdbcType=VARCHAR}
AND		A.MB_EMAIL = #{mb_email, jdbcType=VARCHAR}
